import { useEffect, useState } from "react";
import { Slider } from "@mui/material";
import './weather.css';
import axios from 'axios'
import LoadingSpinner from "../../components/loader/loader";

const Weather = (params) => {
    const [weatherData, setData] = useState()
    const [isLoading, setIsloading] = useState(false);

    useEffect(() => {
        setIsloading(true)
        const getLocation = () => {
            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(getWeatherData, () => { }, {
                    enableHighAccuracy: false,
                    timeout: 5000,
                    maximumAge: Infinity
                });
            } else {
                alert("Geolocation is not supported by this browser.");
            }
        }

        const getWeatherData = async (position) => {
            const { latitude, longitude } = position.coords
            const data = await axios.get(`https://api.openweathermap.org/data/2.5/weather?lat=${latitude.toFixed(2)}&lon=${longitude.toFixed(2)}&units=metric&appid=7827823101d2b73da072f3788992569f`)
            setData(data.data);
        }
        getLocation()
    }, [])

    useEffect(() => {
        if (weatherData) {
            setMarks([
                {
                    value: weatherData.main.temp_min,
                    label: `${weatherData.main.temp_min}°C`,
                },
                {
                    value: weatherData.main.temp,
                    label: `${weatherData.main.temp}°C`,
                },
                {
                    value: weatherData.main.temp_max,
                    label: `${weatherData.main.temp_max}°C`,
                },
            ]
            )
            setIsloading(false)
        }

    }, [weatherData])

    const valuetext = (value) => {
        return `${value}°C`;
    }
    return (isLoading ? <div className="container"><LoadingSpinner></LoadingSpinner></div> :
        (weatherData &&
            <div className="container">
                <div className="weather-content" >
                    <div className="weather-info">
                    <img src={`https://openweathermap.org/img/wn/${weatherData?.weather[0].icon}@2x.png`} alt="weahter" />

                        <div className="weather-data">
                            <h3>Location: <span className="value">{weatherData?.name}</span></h3>
                            <h3>Temprature: <span className="value"> {weatherData?.weather[0].main}</span></h3>
                            <h3>Feel's like: <span className="value">{weatherData?.main?.feels_like}°C</span></h3>
                            <h3>Humidity: <span className="value">{weatherData?.main?.humidity}</span></h3>

                        </div>
                    </div>

                </div>
            </div>
        )
    )
}

export default Weather
