import axios from "axios"
import { useEffect, useState } from "react"
import LoadingSpinner from "../../components/loader/loader";
import './news.css';

const NewsFeed = (params) => {
    const [nData, setData] = useState({});
    const [isLoading, setLoading] = useState(false);

    useEffect(() => {
        (async () => {
            setLoading(true)
            const d = await axios.get("https://newsapi.org/v2/top-headlines?country=us&apiKey=7502218d123b407b92e37ee5c21c8b2f");
            setData(d.data)
            setLoading(false)
        })();
    }, []);

    return (
        isLoading ? <div className="container"><LoadingSpinner/></div> :
            nData &&
            <div className="container">
               <div> <h1>News</h1>
                <div className="articles">
                    {nData.articles?.map((article, index) =>
                        <div className="content">
                            {
                                article?.title ?
                                    <h2 className="title">
                                        {article.title}
                                    </h2>
                                    : <></>
                            }
                            {
                                article?.author ?
                                    <div className="author">
                                        By: {article.author}
                                    </div>
                                    :
                                    <></>
                            }
                            {
                                article?.urlToImage ?
                                    <div className="article-image">
                                        <img src={article.urlToImage} alt={article.title} />
                                    </div>
                                    : <></>
                            }
                            {
                                article?.description ?
                                    <div className="description">
                                        {article.description}
                                    </div>
                                    : <></>
                            }
                        </div>

                    )}
                </div></div>
            </div>
    )
}
export default NewsFeed
