import axios from "axios"
import { useEffect, useState } from "react"
import LoadingSpinner from "../../components/loader/loader";
import './news.css';

const NewsFeed = (params) => {
    const [nData, setData] = useState({});
    const [isLoading, setLoading] = useState(false);

    useEffect(() => {
        (async () => {
            setLoading(true)
            const d = await axios.get("https://newsapi.org/v2/top-headlines?country=us&apiKey=7502218d123b407b92e37ee5c21c8b2f");
            setData(d.data)
            setLoading(false)
        })();
    }, []);

    return (
        isLoading ? <div className="container"><LoadingSpinner/></div> :
            nData &&
            <div className="container">
                  <div>
                    {nData.item?.map((item, index) =>
                      <div className="newsCard">
                                {
                                item?.title ?
                                    <h2 className="title">
                                        {item.title}
                                    </h2>
                                    : <></>
                            }
                            {
                                item?.author ?
                                    <div className="author">
                                        By: {item.author}
                                    </div>
                                    :
                                    <></>
                            }
                            {
                                item?.urlToImage ?
                                    <div className="image">
                                        <img src={item.urlToImage} alt={item.title} />
                                    </div>
                                    : <></>
                            }
                            {
                                item?.description ?
                                    <div className="description">
                                        {item.description}
                                    </div>
                                    : <></>
                            }



                      </div>

                    )}
                </div>
            </div>
    )
}
export default NewsFeed
